import { Component, Input, OnInit } from '@angular/core';
import { TutorialService } from 'src/app/services/tutorial.service';
import { ActivatedRoute, Router } from '@angular/router';
import { Tutorial } from 'src/app/models/tutorial.model';
import { CommentService } from 'src/app/services/comment.service';

@Component({
  selector: 'app-tutorial-details',
  templateUrl: './tutorial-details.component.html',
  styleUrls: ['./tutorial-details.component.css']
})
export class TutorialDetailsComponent implements OnInit {

  @Input() viewMode = false;

  @Input() currentTutorial: Tutorial = {
    title: '',
    description: '',
    published: false
  };

  comments: any;

  comment = {
    name: '',
    text: ''
  };

  message = '';

  constructor(private tutorialService: TutorialService, private commentService: CommentService, private route: ActivatedRoute, private router: Router) {
    this.currentTutorial = new Tutorial();
  }


  ngOnInit(): void {
    if (!this.viewMode) {
      this.message = '';
      const id = this.route.snapshot.paramMap.get('id').toString();
this.getComments(id);
    }
  }

  getComments(id: string): void {
    this.commentService.getComments(id)
      .subscribe({
        next: data => {
          this.comments = data;
          console.log(data);
          if (this.comments.length === 0) {
            console.log('No comments available.');
          }
        },
        error: error => {
          console.log(error);
        }
      });
  }



  // getTutorial(id: Number): void {
  //   this.commentService.getComments(id)
  //     .subscribe({
  //       next: data => {
  //         this.comments = data;
  //         console.log(data);
  //         if (this.comments.length === 0) {
  //           console.log('No comments available.');
  //         }
  //       },
  //       error: error => {
  //         console.log(error);
  //       }
  //   });
  // }


  updatePublished(status: boolean): void {
    const data = {
      title: this.currentTutorial.title,
      description: this.currentTutorial.description,
      published: status
    };

    this.message = '';

    this.tutorialService.update(this.currentTutorial.id, data)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.currentTutorial.published = status;
          this.message = res.message ? res.message : 'The status was updated successfully!';
        },
        error: (e) => console.error(e)
      });
  }

  updateTutorial(): void {
    this.message = '';

    this.tutorialService.update(this.currentTutorial.id, this.currentTutorial)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.message = res.message ? res.message : 'This tutorial was updated successfully!';
        },
        error: (e) => console.error(e)
      });
  }

  deleteTutorial(): void {
    this.tutorialService.delete(this.currentTutorial.id)
      .subscribe({
        next: (res) => {
          console.log(res);
          this.router.navigate(['/tutorials']);
        },
        error: (e) => console.error(e)
      });
  }

  addComment(): void {
    const data = {
      name: this.comment.name,
      text: this.comment.text
    };

    this.commentService.createComment(this.currentTutorial.id, data)
      .subscribe({
        next: response => {
          console.log(response);
          this.comment.name = '';
          this.comment.text = '';
        },
        error: error => {
          console.log(error);
        }
      });
  }
}

<div *ngIf="viewMode">
  <h4>{{ currentTutorial.title }}</h4>

  <p>{{ currentTutorial.description }}</p>

  <p>
    <strong>Status:</strong> {{ currentTutorial.published ? "Published" : "Pending" }}
  </p>

  <a [routerLink]="['/tutorials', currentTutorial.id, 'edit']">Edit</a>
  <br />
  <br />
  <form (ngSubmit)="addComment()" #commentForm="ngForm">
    <div class="form-group">
      <label for="name">Name</label>
      <input type="text" class="form-control" name="name" [(ngModel)]="comment.name" required>
    </div>    
    <div class="form-group">
      <label for="comment">Comment</label>
      <textarea class="form-control" name="comment" [(ngModel)]="comment.text" required></textarea>
    </div>
    <button type="submit" class="btn btn-primary">Add Comment</button>
  </form>

  <div class="comments">
    <h3>Comments:</h3>
    <ul>
      <li *ngFor="let comment of comments">
        <p><strong>{{ comment.name }}</strong></p>
        <p>{{ comment.text }}</p>
      </li>
    </ul>
  </div>

  <p *ngIf="!comments || !comments.length">No comments yet.</p>


  <div *ngIf="currentTutorial.id">
    <h4>Tutorial</h4>
    <div>
      <label><strong>Title:</strong></label> {{ currentTutorial.title }}
    </div>
    <div>
      <label><strong>Description:</strong></label>
      {{ currentTutorial.description }}
    </div>
    <div>
      <label><strong>Status:</strong></label>
      {{ currentTutorial.published ? "Published" : "Pending" }}
    </div>

    <a class="badge badge-warning" routerLink="/tutorials/{{ currentTutorial.id }}">Edit</a>
  </div>

  <div *ngIf="!currentTutorial.id">
    <br />
    <p>Please click on a Tutorial...</p>
  </div>

  <div *ngIf="currentTutorial.id" class="edit-form">
    <form>
      <div class="form-group">
        <label for="title">Title</label>
        <input type="text" class="form-control" id="title" [(ngModel)]="currentTutorial.title" name="title" />
      </div>
      <div class="form-group">
        <label for="description">Description</label>
        <input type="text" class="form-control" id="description" [(ngModel)]="currentTutorial.description" name="description" />
      </div>

      <div class="form-group">
        <label><strong>Status:</strong></label>
        {{ currentTutorial.published ? "Published" : "Pending" }}
      </div>
    </form>

    <button class="badge badge-primary mr-2" *ngIf="currentTutorial.published" (click)="updatePublished(false)">UnPublish</button>
    <button *ngIf="!currentTutorial.published" class="badge badge-primary mr-2" (click)="updatePublished(true)">Publish</button>

    <button class="badge badge-danger mr-2" (click)="deleteTutorial()">Delete</button>

    <button type="submit" class="badge badge-success mb-2" (click)="updateTutorial()">Update</button>

    <p>{{ message }}</p>
  </div>

  <div *ngIf="!currentTutorial.id">
    <br />
    <p>Cannot access this Tutorial...</p>
  </div>
</div>
