import { Component, OnInit } from '@angular/core';
import { CommentService } from '../services/comment.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-comment-list',
  templateUrl: 'src/app/comment-list/comment-list.component.html',
  styleUrls: ['src/app/comment-list/comment-list.component.css']
})

export class CommentFormComponent implements OnInit {
  tutorialId: string;
  comments: any;

  constructor(private commentService: CommentService, private route: ActivatedRoute) {
    this.tutorialId = this.route.snapshot.params["id"];
  }

  ngOnInit(): void {
    this.getComments();
  }

  getComments() {
    this.commentService.getAll(this.tutorialId)
      .subscribe(
        data => {
          this.comments = data;
          console.log(data);
        },
        error => {
          console.log(error);
        });
  }
}
