import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Comment } from '../models/comment.model';
import { CommentService } from '../services/comment.service';

@Component({
  selector: 'app-comment-list',
  templateUrl: './comment-list.component.html',
  styleUrls: ['./comment-list.component.css']
})
export class CommentListComponent implements OnInit {

  tutorialId!: string;
  comments!: Comment[];
  commentForm: FormGroup;

  constructor(private commentService: CommentService, private fb: FormBuilder, private route: ActivatedRoute) { }

  ngOnInit(): void {
    // Get tutorial ID from route parameter
    this.tutorialId = this.route.snapshot.paramMap.get('id');

    // Load comments for this tutorial
    this.commentService.getAll(this.tutorialId)
      .subscribe(
        data => {
          this.comments = data;
        },
        error => {
          console.log(error);
        });

    // Initialize comment form
    this.commentForm = this.fb.group({
      text: ['', Validators.required]
    });
  }

  addComment() {
    const comment = {
      text: this.commentForm.value.text,
      tutorialId: this.tutorialId
    };
    this.commentService.create(comment)
      .subscribe(
        response => {
          console.log(response);
          this.comments.push(response);
          this.commentForm.reset();
        },
        error => {
          console.log(error);
        });
  }

}
<div>
  <h3>Comments</h3>
  <table class="table table-bordered table-striped">
    <thead>
      <tr>
        <th>Id</th>
        <th>Text</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let comment of comments">
        <td>{{ comment.id }}</td>
        <td>{{ comment.text }}</td>
      </tr>
    </tbody>
  </table>
  <form [formGroup]="commentForm" (ngSubmit)="addComment()">
    <div class="form-group">
      <label for="text">Comment Text:</label>
      <input type="text" formControlName="text" class="form-control">
    </div>
    <button type="submit" class="btn btn-primary">Add Comment</button>
  </form>
</div>
