import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';

const baseUrl = 'http://localhost:8080/api/comments';

@Injectable({
  providedIn: 'root'
})
export class CommentService {

  constructor(private http: HttpClient) { }

  getAll(tutorialId: any): Observable<any> {
    return this.http.get(`${baseUrl}?tutorialId=${tutorialId}`);
  }

  create(tutorialId: any, data: any): Observable<any> {
    return this.http.post(`${baseUrl}?tutorialId=${tutorialId}`, data);
}


  update(id: any, data: any): Observable<any> {
    return this.http.put(`${baseUrl}/${id}`, data);
  }

  delete(id: any): Observable<any> {
    return this.http.delete(`${baseUrl}/${id}`);
  }

  deleteAll(): Observable<any> {
    return this.http.delete(baseUrl);
  }

}
