import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { CommentService } from '../services/comment.service';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-comment-form',
  templateUrl: './comment-form.component.html',
  styleUrls: ['./comment-form.component.css']
})

export class CommentFormComponent implements OnInit {
  tutorialId: string;
  comments: any[] = [];
  newComment: any = {};
  submitted: boolean = false;
  commentForm: FormGroup;

  constructor(private formBuilder: FormBuilder, private commentService: CommentService, private route: ActivatedRoute) {
    this.tutorialId = this.route.snapshot.params["id"];
    this.commentForm = this.formBuilder.group({
      content: ['', Validators.required]
    });
  }

  ngOnInit(): void {
    this.getComments();
  }

  getComments() {
    this.commentService.getComments(this.tutorialId)
      .subscribe(
        data => {
          this.comments = data;
          console.log(data);
        },
        error => {
          console.log(error);
        });
  }

  submitComment() {
    this.commentService.createComment(this.tutorialId, this.commentForm.value)
      .subscribe(
        response => {
          console.log(response);
          this.submitted = true;
          this.commentForm.reset();
          this.getComments();
        },
        error => {
          console.log(error);
        });
  }
}
<form [formGroup]="commentForm" (ngSubmit)="submitComment()">
